import { sql } from "drizzle-orm";
import { 
  pgTable, 
  text, 
  varchar, 
  jsonb, 
  timestamp, 
  integer, 
  boolean, 
  real,
  index
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)]
);

// Enhanced users table for Replit Auth
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  username: text("username").unique(),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const aiProviders = pgTable("ai_providers", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  provider: text("provider").notNull(), // 'openai', 'gemini', 'grok'
  apiKey: text("api_key").notNull(),
  isActive: boolean("is_active").default(false),
  createdAt: timestamp("created_at").defaultNow(),
});

export const businessAnalyses = pgTable("business_analyses", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  url: text("url").notNull(),
  businessModel: text("business_model"),
  revenueStream: text("revenue_stream"),
  targetMarket: text("target_market"),
  overallScore: real("overall_score"),
  scoreDetails: jsonb("score_details"),
  aiInsights: jsonb("ai_insights"),
  currentStage: integer("current_stage").default(1),
  stageData: jsonb("stage_data"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const workflowStages = pgTable("workflow_stages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  analysisId: varchar("analysis_id").notNull(),
  stageNumber: integer("stage_number").notNull(),
  stageName: text("stage_name").notNull(),
  status: text("status").default("pending"), // 'pending', 'in_progress', 'completed'
  data: jsonb("data"),
  aiGeneratedContent: jsonb("ai_generated_content"),
  completedAt: timestamp("completed_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

// User schema for Replit Auth
export const upsertUserSchema = createInsertSchema(users).pick({
  id: true,
  email: true,
  firstName: true,
  lastName: true,
  profileImageUrl: true,
  username: true,
});

export const insertAiProviderSchema = createInsertSchema(aiProviders).pick({
  userId: true,
  provider: true,
  apiKey: true,
  isActive: true,
});

export const insertBusinessAnalysisSchema = createInsertSchema(businessAnalyses).pick({
  userId: true,
  url: true,
  businessModel: true,
  revenueStream: true,
  targetMarket: true,
  overallScore: true,
  scoreDetails: true,
  aiInsights: true,
  currentStage: true,
  stageData: true,
});

export const insertWorkflowStageSchema = createInsertSchema(workflowStages).pick({
  analysisId: true,
  stageNumber: true,
  stageName: true,
  status: true,
  data: true,
  aiGeneratedContent: true,
});

export type User = typeof users.$inferSelect;
export type UpsertUser = z.infer<typeof upsertUserSchema>;
export type AiProvider = typeof aiProviders.$inferSelect;
export type InsertAiProvider = z.infer<typeof insertAiProviderSchema>;
export type BusinessAnalysis = typeof businessAnalyses.$inferSelect;
export type InsertBusinessAnalysis = z.infer<typeof insertBusinessAnalysisSchema>;
export type WorkflowStage = typeof workflowStages.$inferSelect;
export type InsertWorkflowStage = z.infer<typeof insertWorkflowStageSchema>;

