  .border-vc-primary {
    border-color: var(--vc-primary);
  }

  .border-vc-secondary {
    border-color: var(--vc-secondary);
  }

  .border-vc-accent {
    border-color: var(--vc-accent);
  }

  .border-vc-border {
    border-color: var(--vc-border);
  }
