client/src/components/ai-provider-modal..content:
import { useState, useEffect } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Eye, EyeOff, X, Link } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { AIService } from "@/lib/ai-service";
import type { AIProvider } from "@/types";

interface AIProviderModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export function AIProviderModal({ isOpen, onClose }: AIProviderModalProps) {
  const [selectedProvider, setSelectedProvider] = useState<'openai' | 'gemini' | 'grok'>('openai');
  const [apiKey, setApiKey] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isTestingConnection, setIsTestingConnection] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: providers = [] } = useQuery({
    queryKey: ['/api/ai-providers'],
    queryFn: () => AIService.getProviders(),
  });

  const { data: activeProvider } = useQuery({
    queryKey: ['/api/ai-providers/active'],
    queryFn: () => AIService.getActiveProvider(),
  });

  useEffect(() => {
    if (activeProvider) {
      setSelectedProvider(activeProvider.provider);
      setApiKey(activeProvider.apiKey);
    }
  }, [activeProvider]);

  const saveProviderMutation = useMutation({
    mutationFn: async (data: { provider: string; apiKey: string; isActive: boolean }) => {
      // Check if provider already exists
      const existingProvider = providers.find(p => p.provider === data.provider);

      if (existingProvider) {
        return await AIService.updateProvider(existingProvider.id, {
          apiKey: data.apiKey,
          isActive: data.isActive
        });
      } else {
        return await AIService.saveProvider({
          provider: data.provider as any,
          apiKey: data.apiKey,
          isActive: data.isActive
        });
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/ai-providers'] });
      queryClient.invalidateQueries({ queryKey: ['/api/ai-providers/active'] });
      toast({
        title: "Success",
        description: "AI provider configuration saved successfully",
      });
      onClose();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to save AI provider configuration",
        variant: "destructive",
      });
    },
  });

  const handleTestConnection = async () => {
    if (!apiKey.trim()) {
      toast({
        title: "Error",
        description: "Please enter an API key",
        variant: "destructive",
      });
      return;
    }

    setIsTestingConnection(true);
    try {
      const isConnected = await AIService.testConnection(selectedProvider, apiKey);

      if (isConnected) {
        toast({
          title: "Success",
          description: "Connection test successful",
        });
      } else {
        toast({
          title: "Error",
          description: "Connection test failed. Please check your API key.",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Error",
        description: "Connection test failed",
        variant: "destructive",
      });
    } finally {
      setIsTestingConnection(false);
    }
  };

  const handleSave = () => {
    if (!apiKey.trim()) {
      toast({
        title: "Error",
        description: "Please enter an API key",
        variant: "destructive",
      });
      return;
    }

    saveProviderMutation.mutate({
      provider: selectedProvider,
      apiKey: apiKey.trim(),
      isActive: true
    });
  };

  const handleClose = () => {
    setApiKey('');
    setShowPassword(false);
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="bg-vc-card border-vc-border max-w-md text-vc-text" data-testid="modal-ai-provider">
        <DialogHeader>
          <div className="flex items-center justify-between">
            <DialogTitle className="text-lg font-semibold text-vc-text">
              AI Provider Configuration
            </DialogTitle>
            <Button
              variant="ghost"
              size="icon"
              onClick={handleClose}
              className="text-vc-text-muted hover:text-vc-text"
              data-testid="button-close-modal"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
          <DialogDescription className="text-sm text-vc-text-muted">
            Configure your AI provider settings and API keys for business analysis.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* Provider Selection */}
          <div>
            <Label className="text-sm font-medium text-vc-text mb-2">AI Provider</Label>
            <Select value={selectedProvider} onValueChange={(value: any) => setSelectedProvider(value)}>
              <SelectTrigger className="w-full bg-vc-dark border-vc-border text-vc-text focus:border-vc-primary" data-testid="select-provider">
                <SelectValue />
              </SelectTrigger>
              <SelectContent className="bg-vc-card border-vc-border">
                <SelectItem value="openai" className="text-vc-text">OpenAI GPT-4</SelectItem>
                <SelectItem value="gemini" className="text-vc-text">Google Gemini 2.5 Pro</SelectItem>
                <SelectItem value="grok" className="text-vc-text">xAI Grok 4</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* API Key Input */}
          <div>
            <Label className="text-sm font-medium text-vc-text mb-2">API Key</Label>
            <div className="relative">
              <Input
                type={showPassword ? "text" : "password"}
                placeholder="Enter your API key"
                value={apiKey}
                onChange={(e) => setApiKey(e.target.value)}
                className="w-full bg-vc-dark border-vc-border pr-10 text-vc-text focus:border-vc-primary"
                data-testid="input-api-key"
              />
              <Button
                type="button"
                variant="ghost"
                size="icon"
                className="absolute right-2 top-1/2 -translate-y-1/2 text-vc-text-muted hover:text-vc-text h-8 w-8"
                onClick={() => setShowPassword(!showPassword)}
                data-testid="button-toggle-password"
              >
                {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
              </Button>
            </div>
            <p className="text-xs text-vc-text-muted mt-1">
              Your API key is stored locally and never shared
            </p>
          </div>

          {/* Test Connection */}
          <Button
            variant="outline"
            className="w-full bg-vc-primary/20 border-vc-primary text-vc-primary hover:bg-vc-primary/30 transition-colors"
            onClick={handleTestConnection}
            disabled={isTestingConnection || !apiKey.trim()}
            data-testid="button-test-connection"
          >
            <Link className="mr-2 h-4 w-4" />
            {isTestingConnection ? 'Testing...' : 'Test Connection'}
          </Button>
        </div>

        <div className="flex space-x-3 mt-6">
          <Button
            variant="outline"
            className="flex-1 bg-vc-border text-vc-text hover:bg-vc-border/80 transition-colors"
            onClick={handleClose}
            data-testid="button-cancel"
          >
            Cancel
          </Button>
          <Button
            className="flex-1 bg-vc-primary hover:bg-vc-primary/80 text-white transition-colors shadow-neon"
            onClick={handleSave}
            disabled={saveProviderMutation.isPending || !apiKey.trim()}
            data-testid="button-save-configuration"
          >
            {saveProviderMutation.isPending ? 'Saving...' : 'Save Configuration'}
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/analytics-charts.t.content:
import { useQuery } from "@tanstack/react-query";
import { 
  BarChart, Bar, LineChart, Line, PieChart, Pie, RadarChart, Radar,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer,
  Cell, PolarGrid, PolarAngleAxis, PolarRadiusAxis
} from "recharts";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { TrendingUp, Activity, Zap, Target } from "lucide-react";
import type { BusinessAnalysis } from "@/types";

interface AnalyticsChartsProps {
  analyses: BusinessAnalysis[];
}

export function AnalyticsCharts({ analyses }: AnalyticsChartsProps) {
  const COLORS = ['#FF4500', '#DC143C', '#FFD700', '#20B2AA', '#9370DB'];

  // Prepare data for score distribution
  const scoreDistribution = [
    { range: '0-2', count: analyses.filter(a => (a.overallScore || 0) <= 2).length },
    { range: '3-4', count: analyses.filter(a => (a.overallScore || 0) > 2 && (a.overallScore || 0) <= 4).length },
    { range: '5-6', count: analyses.filter(a => (a.overallScore || 0) > 4 && (a.overallScore || 0) <= 6).length },
    { range: '7-8', count: analyses.filter(a => (a.overallScore || 0) > 6 && (a.overallScore || 0) <= 8).length },
    { range: '9-10', count: analyses.filter(a => (a.overallScore || 0) > 8).length },
  ];

  // Prepare data for criteria comparison
  const criteriaComparison = analyses.length > 0 ? [
    {
      criteria: 'Technical',
      average: analyses.reduce((acc, a) => acc + (a.scoreDetails?.technicalComplexity?.score || 0), 0) / analyses.length,
    },
    {
      criteria: 'Market',
      average: analyses.reduce((acc, a) => acc + (a.scoreDetails?.marketOpportunity?.score || 0), 0) / analyses.length,
    },
    {
      criteria: 'Competition',
      average: analyses.reduce((acc, a) => acc + (a.scoreDetails?.competitiveLandscape?.score || 0), 0) / analyses.length,
    },
    {
      criteria: 'Resources',
      average: analyses.reduce((acc, a) => acc + (a.scoreDetails?.resourceRequirements?.score || 0), 0) / analyses.length,
    },
    {
      criteria: 'Time',
      average: analyses.reduce((acc, a) => acc + (a.scoreDetails?.timeToMarket?.score || 0), 0) / analyses.length,
    },
  ] : [];

  // Prepare data for stage progress
  const stageProgress = [
    { stage: 'Discovery', count: analyses.filter(a => a.currentStage === 1).length },
    { stage: 'Filter', count: analyses.filter(a => a.currentStage === 2).length },
    { stage: 'MVP', count: analyses.filter(a => a.currentStage === 3).length },
    { stage: 'Testing', count: analyses.filter(a => a.currentStage === 4).length },
    { stage: 'Scaling', count: analyses.filter(a => a.currentStage === 5).length },
    { stage: 'Automation', count: analyses.filter(a => a.currentStage === 6).length },
  ];

  // Prepare radar chart data for top analyses
  const topAnalyses = analyses
    .filter(a => a.scoreDetails)
    .sort((a, b) => (b.overallScore || 0) - (a.overallScore || 0))
    .slice(0, 3);

  const radarData = topAnalyses.length > 0 ? [
    'technicalComplexity',
    'marketOpportunity',
    'competitiveLandscape',
    'resourceRequirements',
    'timeToMarket'
  ].map(key => ({
    criteria: key.replace(/([A-Z])/g, ' $1').trim(),
    ...topAnalyses.reduce((acc, analysis, index) => ({
      ...acc,
      [`business${index + 1}`]: analysis.scoreDetails?.[key as keyof typeof analysis.scoreDetails]?.score || 0
    }), {})
  })) : [];

  if (analyses.length === 0) {
    return (
      <Card className="bg-vc-card border-vc-border" data-testid="card-analytics">
        <CardHeader>
          <CardTitle className="text-lg font-semibold text-vc-text">Analytics Dashboard</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-12">
            <div className="text-5xl mb-4">ðŸ“Š</div>
            <p className="text-vc-text-muted">No data available yet</p>
            <p className="text-vc-text-muted text-sm mt-1">
              Start analyzing businesses to see insights
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6" data-testid="analytics-charts">
      {/* Score Distribution */}
      <Card className="bg-vc-card border-vc-border">
        <CardHeader>
          <CardTitle className="text-lg font-semibold text-vc-text flex items-center">
            <TrendingUp className="mr-2 h-5 w-5 text-vc-primary" />
            Score Distribution
          </CardTitle>
        </CardHeader>
        <CardContent>
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={scoreDistribution}>
              <CartesianGrid strokeDasharray="3 3" stroke="#333" />
              <XAxis dataKey="range" stroke="#999" />
              <YAxis stroke="#999" />
              <Tooltip 
                contentStyle={{ backgroundColor: '#1A1A1A', border: '1px solid #333' }}
                labelStyle={{ color: '#FFD700' }}
              />
              <Bar dataKey="count" fill="#FF4500">
                {scoreDistribution.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Bar>
            </BarChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Criteria Comparison */}
        <Card className="bg-vc-card border-vc-border">
          <CardHeader>
            <CardTitle className="text-lg font-semibold text-vc-text flex items-center">
              <Activity className="mr-2 h-5 w-5 text-vc-secondary" />
              Average Scores by Criteria
            </CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={200}>
              <LineChart data={criteriaComparison}>
                <CartesianGrid strokeDasharray="3 3" stroke="#333" />
                <XAxis dataKey="criteria" stroke="#999" />
                <YAxis domain={[0, 10]} stroke="#999" />
                <Tooltip 
                  contentStyle={{ backgroundColor: '#1A1A1A', border: '1px solid #333' }}
                  labelStyle={{ color: '#FFD700' }}
                  formatter={(value: number) => value.toFixed(1)}
                />
                <Line 
                  type="monotone" 
                  dataKey="average" 
                  stroke="#DC143C" 
                  strokeWidth={2}
                  dot={{ fill: '#FFD700', r: 4 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        {/* Stage Progress */}
        <Card className="bg-vc-card border-vc-border">
          <CardHeader>
            <CardTitle className="text-lg font-semibold text-vc-text flex items-center">
              <Zap className="mr-2 h-5 w-5 text-vc-accent" />
              Workflow Stage Distribution
            </CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={200}>
              <PieChart>
                <Pie
                  data={stageProgress.filter(s => s.count > 0)}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ stage, count }) => `${stage}: ${count}`}
                  outerRadius={70}
                  fill="#8884d8"
                  dataKey="count"
                >
                  {stageProgress.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip 
                  contentStyle={{ backgroundColor: '#1A1A1A', border: '1px solid #333' }}
                  labelStyle={{ color: '#FFD700' }}
                />
              </PieChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>
      </div>

      {/* Radar Comparison for Top 3 */}
      {topAnalyses.length > 0 && (
        <Card className="bg-vc-card border-vc-border">
          <CardHeader>
            <CardTitle className="text-lg font-semibold text-vc-text flex items-center">
              <Target className="mr-2 h-5 w-5 text-vc-primary" />
              Top Businesses Comparison
            </CardTitle>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={300}>
              <RadarChart data={radarData}>
                <PolarGrid stroke="#333" />
                <PolarAngleAxis dataKey="criteria" stroke="#999" />
                <PolarRadiusAxis angle={90} domain={[0, 10]} stroke="#999" />
                {topAnalyses.map((analysis, index) => (
                  <Radar
                    key={index}
                    name={analysis.businessModel || `Business ${index + 1}`}
                    dataKey={`business${index + 1}`}
                    stroke={COLORS[index]}
                    fill={COLORS[index]}
                    fillOpacity={0.3}
                  />
                ))}
                <Tooltip 
                  contentStyle={{ backgroundColor: '#1A1A1A', border: '1px solid #333' }}
                  labelStyle={{ color: '#FFD700' }}
                />
                <Legend 
                  wrapperStyle={{ color: '#999' }}
                />
              </RadarChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/batch-analysis.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Upload, FileText, Play, AlertCircle, CheckCircle2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { AIService } from "@/lib/ai-service";

interface BatchAnalysisProps {
  onAnalysisComplete?: (analysisId: string) => void;
}

export function BatchAnalysis({ onAnalysisComplete }: BatchAnalysisProps) {
  const [urls, setUrls] = useState<string>("");
  const [processingUrls, setProcessingUrls] = useState<string[]>([]);
  const [completedUrls, setCompletedUrls] = useState<{ url: string; id: string; success: boolean }[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const { toast } = useToast();

  const analyzeUrlMutation = useMutation({
    mutationFn: async (url: string) => {
      const response = await apiRequest('/api/business-analyses', 'POST', {
        url,
        businessModel: 'E-commerce Platform',
        revenueStream: 'Subscription-based',
        targetMarket: 'B2B',
        overallScore: 7,
        scoreDetails: {
          technicalComplexity: { score: 6, reasoning: 'Moderate complexity' },
          marketOpportunity: { score: 8, reasoning: 'Large market' },
          competitiveLandscape: { score: 7, reasoning: 'Competitive but viable' },
          resourceRequirements: { score: 7, reasoning: 'Moderate resources needed' },
          timeToMarket: { score: 8, reasoning: 'Quick to launch' }
        },
        aiInsights: {
          keyInsight: 'Strong market opportunity',
          riskFactor: 'Competition is increasing',
          opportunity: 'Niche market potential'
        }
      });

      return { url, id: response.id };
    },
    onSuccess: (data) => {
      setCompletedUrls(prev => [...prev, { ...data, success: true }]);
      queryClient.invalidateQueries({ queryKey: ['/api/business-analyses'] });
      queryClient.invalidateQueries({ queryKey: ['/api/stats'] });
    },
    onError: (error, url) => {
      setCompletedUrls(prev => [...prev, { url, id: '', success: false }]);
      console.error(`Failed to analyze ${url}:`, error);
    },
  });

  const handleBatchAnalysis = async () => {
    const urlList = urls
      .split('\n')
      .map(url => url.trim())
      .filter(url => url && (url.startsWith('http://') || url.startsWith('https://')));

    if (urlList.length === 0) {
      toast({
        title: "Invalid URLs",
        description: "Please enter valid URLs starting with http:// or https://",
        variant: "destructive",
      });
      return;
    }

    if (urlList.length > 10) {
      toast({
        title: "Too Many URLs",
        description: "Please limit batch analysis to 10 URLs at a time",
        variant: "destructive",
      });
      return;
    }

    setIsProcessing(true);
    setProcessingUrls(urlList);
    setCompletedUrls([]);

    for (const url of urlList) {
      try {
        await analyzeUrlMutation.mutateAsync(url);
      } catch (error) {
        // Error handled in mutation
      }
    }

    setIsProcessing(false);
    toast({
      title: "Batch Analysis Complete",
      description: `Analyzed ${completedUrls.filter(c => c.success).length} of ${urlList.length} URLs successfully`,
    });

    // Select the first successful analysis
    const firstSuccess = completedUrls.find(c => c.success);
    if (firstSuccess && onAnalysisComplete) {
      onAnalysisComplete(firstSuccess.id);
    }
  };

  const progress = processingUrls.length > 0 
    ? (completedUrls.length / processingUrls.length) * 100 
    : 0;

  return (
    <Card className="bg-vc-card border-vc-border" data-testid="card-batch-analysis">
      <CardHeader>
        <CardTitle className="text-lg font-semibold text-vc-text flex items-center">
          <Upload className="mr-2 h-5 w-5 text-vc-primary" />
          Batch Analysis
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div>
          <label className="text-sm text-vc-text-muted mb-2 block">
            Enter URLs (one per line, max 10)
          </label>
          <Textarea
            value={urls}
            onChange={(e) => setUrls(e.target.value)}
            placeholder="https://example.com&#10;https://another-site.com&#10;https://third-site.com"
            className="bg-vc-dark border-vc-border text-vc-text placeholder:text-vc-text-muted h-32 font-mono text-sm"
            disabled={isProcessing}
            data-testid="input-batch-urls"
          />
        </div>

        {isProcessing && (
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-sm text-vc-text-muted">Processing...</span>
              <span className="text-sm text-vc-accent font-medium">
                {completedUrls.length}/{processingUrls.length}
              </span>
            </div>
            <Progress value={progress} className="h-2" />
          </div>
        )}

        {completedUrls.length > 0 && (
          <div className="space-y-2">
            <p className="text-sm font-medium text-vc-text">Results:</p>
            <div className="space-y-1 max-h-40 overflow-y-auto">
              {completedUrls.map((result, index) => (
                <div
                  key={index}
                  className="flex items-center justify-between p-2 bg-vc-dark rounded border border-vc-border/50"
                  data-testid={`batch-result-${index}`}
                >
                  <span className="text-xs text-vc-text-muted truncate flex-1 mr-2 font-mono">
                    {result.url}
                  </span>
                  {result.success ? (
                    <CheckCircle2 className="h-4 w-4 text-green-400" />
                  ) : (
                    <AlertCircle className="h-4 w-4 text-red-400" />
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        <Button
          onClick={handleBatchAnalysis}
          disabled={isProcessing || !urls.trim()}
          className="w-full bg-gradient-to-r from-vc-primary to-vc-secondary hover:opacity-90"
          data-testid="button-start-batch"
        >
          {isProcessing ? (
            <>
              <FileText className="mr-2 h-4 w-4 animate-pulse" />
              Analyzing {completedUrls.length + 1} of {processingUrls.length}...
            </>
          ) : (
            <>
              <Play className="mr-2 h-4 w-4" />
              Start Batch Analysis
            </>
          )}
        </Button>

        {!isProcessing && urls.trim() && (
          <p className="text-xs text-vc-text-muted text-center">
            {urls.split('\n').filter(u => u.trim()).length} URLs ready for analysis
          </p>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/business-compariso.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { ArrowUpDown, Percent, TrendingUp, Clock, DollarSign, Users } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import type { BusinessAnalysis } from "@/types";

interface BusinessComparisonProps {
  analyses: BusinessAnalysis[];
}

export function BusinessComparison({ analyses }: BusinessComparisonProps) {
  const [selectedAnalyses, setSelectedAnalyses] = useState<string[]>([]);
  const [sortBy, setSortBy] = useState<'score' | 'date' | 'stage'>('score');

  const toggleSelection = (id: string) => {
    setSelectedAnalyses(prev => 
      prev.includes(id) 
        ? prev.filter(a => a !== id)
        : [...prev, id]
    );
  };

  const selectedItems = analyses.filter(a => selectedAnalyses.includes(a.id));

  const sortedAnalyses = [...analyses].sort((a, b) => {
    switch (sortBy) {
      case 'score':
        return (b.overallScore || 0) - (a.overallScore || 0);
      case 'date':
        return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
      case 'stage':
        return (b.currentStage || 1) - (a.currentStage || 1);
      default:
        return 0;
    }
  });

  const getAverageScore = (items: BusinessAnalysis[], key: string) => {
    const scores = items
      .filter(item => item.scoreDetails)
      .map(item => {
        const detail = item.scoreDetails![key as keyof typeof item.scoreDetails] as any;
        return detail?.score || 0;
      });

    if (scores.length === 0) return 0;
    return Math.round((scores.reduce((a, b) => a + b, 0) / scores.length) * 10) / 10;
  };

  const getScoreColor = (score: number) => {
    if (score >= 8) return 'text-green-400';
    if (score >= 6) return 'text-yellow-400';
    return 'text-red-400';
  };

  if (analyses.length < 2) {
    return (
      <Card className="bg-vc-card border-vc-border" data-testid="card-business-comparison">
        <CardHeader>
          <CardTitle className="text-lg font-semibold text-vc-text">Business Comparison</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8">
            <div className="text-4xl mb-4">ðŸ“Š</div>
            <p className="text-vc-text-muted">Need at least 2 analyses to compare</p>
            <p className="text-vc-text-muted text-sm mt-1">
              Analyze more businesses to unlock comparison features
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-vc-card border-vc-border" data-testid="card-business-comparison">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg font-semibold text-vc-text">Business Comparison</CardTitle>
          <div className="flex items-center space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setSortBy('score')}
              className={`border-vc-border ${sortBy === 'score' ? 'bg-vc-primary/20 border-vc-primary' : ''}`}
              data-testid="button-sort-score"
            >
              <ArrowUpDown className="h-3 w-3 mr-1" />
              Score
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setSortBy('date')}
              className={`border-vc-border ${sortBy === 'date' ? 'bg-vc-primary/20 border-vc-primary' : ''}`}
              data-testid="button-sort-date"
            >
              <Clock className="h-3 w-3 mr-1" />
              Date
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setSortBy('stage')}
              className={`border-vc-border ${sortBy === 'stage' ? 'bg-vc-primary/20 border-vc-primary' : ''}`}
              data-testid="button-sort-stage"
            >
              <TrendingUp className="h-3 w-3 mr-1" />
              Stage
            </Button>
          </div>
        </div>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Selection List */}
        <div className="space-y-2 max-h-60 overflow-y-auto">
          {sortedAnalyses.map((analysis) => (
            <div
              key={analysis.id}
              className={`flex items-center space-x-3 p-3 bg-vc-dark rounded-lg border ${
                selectedAnalyses.includes(analysis.id) 
                  ? 'border-vc-primary bg-vc-primary/10' 
                  : 'border-vc-border/50'
              } hover:border-vc-border transition-colors cursor-pointer`}
              onClick={() => toggleSelection(analysis.id)}
              data-testid={`comparison-item-${analysis.id}`}
            >
              <Checkbox
                checked={selectedAnalyses.includes(analysis.id)}
                onCheckedChange={() => toggleSelection(analysis.id)}
                className="border-vc-border data-[state=checked]:bg-vc-primary"
              />
              <div className="flex-1">
                <p className="text-vc-text font-medium text-sm">
                  {analysis.businessModel || 'Business Analysis'}
                </p>
                <p className="text-vc-text-muted text-xs font-mono truncate">
                  {analysis.url}
                </p>
              </div>
              <Badge className={`${
                (analysis.overallScore || 0) >= 7 
                  ? 'bg-green-900/50 text-green-300' 
                  : (analysis.overallScore || 0) >= 5 
                  ? 'bg-yellow-900/50 text-yellow-300' 
                  : 'bg-red-900/50 text-red-300'
              } text-xs`}>
                {analysis.overallScore}/10
              </Badge>
            </div>
          ))}
        </div>

        {/* Comparison Results */}
        {selectedItems.length >= 2 && (
          <div className="border-t border-vc-border pt-4">
            <h3 className="text-sm font-semibold text-vc-text mb-3">
              Comparing {selectedItems.length} Businesses
            </h3>

            {/* Average Scores Grid */}
            <div className="grid grid-cols-2 gap-3 mb-4">
              <div className="bg-vc-dark rounded-lg p-3 border border-vc-border/50">
                <div className="flex items-center justify-between mb-1">
                  <span className="text-xs text-vc-text-muted">Avg. Overall Score</span>
                  <Percent className="h-3 w-3 text-vc-text-muted" />
                </div>
                <p className={`text-xl font-bold ${getScoreColor(
                  selectedItems.reduce((acc, item) => acc + (item.overallScore || 0), 0) / selectedItems.length
                )}`}>
                  {Math.round((selectedItems.reduce((acc, item) => acc + (item.overallScore || 0), 0) / selectedItems.length) * 10) / 10}
                </p>
              </div>

              <div className="bg-vc-dark rounded-lg p-3 border border-vc-border/50">
                <div className="flex items-center justify-between mb-1">
                  <span className="text-xs text-vc-text-muted">Avg. Tech Complexity</span>
                  <TrendingUp className="h-3 w-3 text-vc-text-muted" />
                </div>
                <p className={`text-xl font-bold ${getScoreColor(
                  getAverageScore(selectedItems, 'technicalComplexity')
                )}`}>
                  {getAverageScore(selectedItems, 'technicalComplexity')}
                </p>
              </div>

              <div className="bg-vc-dark rounded-lg p-3 border border-vc-border/50">
                <div className="flex items-center justify-between mb-1">
                  <span className="text-xs text-vc-text-muted">Avg. Market Opportunity</span>
                  <Users className="h-3 w-3 text-vc-text-muted" />
                </div>
                <p className={`text-xl font-bold ${getScoreColor(
                  getAverageScore(selectedItems, 'marketOpportunity')
                )}`}>
                  {getAverageScore(selectedItems, 'marketOpportunity')}
                </p>
              </div>

              <div className="bg-vc-dark rounded-lg p-3 border border-vc-border/50">
                <div className="flex items-center justify-between mb-1">
                  <span className="text-xs text-vc-text-muted">Avg. Time to Market</span>
                  <Clock className="h-3 w-3 text-vc-text-muted" />
                </div>
                <p className={`text-xl font-bold ${getScoreColor(
                  getAverageScore(selectedItems, 'timeToMarket')
                )}`}>
                  {getAverageScore(selectedItems, 'timeToMarket')}
                </p>
              </div>
            </div>

            {/* Best Candidate */}
            <div className="bg-gradient-to-r from-vc-primary/20 to-vc-secondary/20 rounded-lg p-3 border border-vc-primary/30">
              <p className="text-xs text-vc-text-muted mb-1">Best Overall Candidate</p>
              <p className="text-sm font-semibold text-vc-text">
                {selectedItems.reduce((best, current) => 
                  (current.overallScore || 0) > (best.overallScore || 0) ? current : best
                ).businessModel || 'Unknown'}
              </p>
              <p className="text-xs text-vc-accent mt-1">
                Score: {selectedItems.reduce((best, current) => 
                  (current.overallScore || 0) > (best.overallScore || 0) ? current : best
                ).overallScore}/10
              </p>
            </div>
          </div>
        )}

        {selectedItems.length === 1 && (
          <div className="text-center py-4 border-t border-vc-border">
            <p className="text-vc-text-muted text-sm">
              Select at least 2 businesses to compare
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/export-analysis.ts.content:
import { useState } from "react";
import { Download, FileText, FileSpreadsheet, FileJson } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useToast } from "@/hooks/use-toast";
import type { BusinessAnalysis } from "@/types";

interface ExportAnalysisProps {
  analysis: BusinessAnalysis;
  stages?: any[];
}

export function ExportAnalysis({ analysis, stages = [] }: ExportAnalysisProps) {
  const [isExporting, setIsExporting] = useState(false);
  const { toast } = useToast();

  const generatePDF = () => {
    // Create HTML content for PDF
    const htmlContent = `
      <html>
        <head>
          <style>
            body { font-family: Arial, sans-serif; padding: 20px; }
            h1 { color: #FF4500; }
            h2 { color: #DC143C; margin-top: 20px; }
            h3 { color: #333; margin-top: 15px; }
            .score { background: #f0f0f0; padding: 10px; border-radius: 5px; margin: 10px 0; }
            .insight { background: #fff3e0; padding: 10px; border-radius: 5px; margin: 10px 0; }
            table { width: 100%; border-collapse: collapse; margin: 10px 0; }
            th, td { border: 1px solid #ddd; padding: 8px; text-align: left; }
            th { background-color: #f2f2f2; }
          </style>
        </head>
        <body>
          <h1>Business Analysis Report</h1>
          <h2>Business Overview</h2>
          <p><strong>URL:</strong> ${analysis.url}</p>
          <p><strong>Business Model:</strong> ${analysis.businessModel || 'N/A'}</p>
          <p><strong>Revenue Stream:</strong> ${analysis.revenueStream || 'N/A'}</p>
          <p><strong>Target Market:</strong> ${analysis.targetMarket || 'N/A'}</p>
          <div class="score">
            <h3>Overall Score: ${analysis.overallScore}/10</h3>
          </div>

          ${analysis.scoreDetails ? `
            <h2>Detailed Scoring</h2>
            <table>
              <tr>
                <th>Criteria</th>
                <th>Score</th>
                <th>Reasoning</th>
              </tr>
              <tr>
                <td>Technical Complexity</td>
                <td>${analysis.scoreDetails.technicalComplexity.score}/10</td>
                <td>${analysis.scoreDetails.technicalComplexity.reasoning}</td>
              </tr>
              <tr>
                <td>Market Opportunity</td>
                <td>${analysis.scoreDetails.marketOpportunity.score}/10</td>
                <td>${analysis.scoreDetails.marketOpportunity.reasoning}</td>
              </tr>
              <tr>
                <td>Competitive Landscape</td>
                <td>${analysis.scoreDetails.competitiveLandscape.score}/10</td>
                <td>${analysis.scoreDetails.competitiveLandscape.reasoning}</td>
              </tr>
              <tr>
                <td>Resource Requirements</td>
                <td>${analysis.scoreDetails.resourceRequirements.score}/10</td>
                <td>${analysis.scoreDetails.resourceRequirements.reasoning}</td>
              </tr>
              <tr>
                <td>Time to Market</td>
                <td>${analysis.scoreDetails.timeToMarket.score}/10</td>
                <td>${analysis.scoreDetails.timeToMarket.reasoning}</td>
              </tr>
            </table>
          ` : ''}

          ${analysis.aiInsights ? `
            <h2>AI Insights</h2>
            <div class="insight">
              <h3>Key Insight</h3>
              <p>${analysis.aiInsights.keyInsight}</p>
            </div>
            <div class="insight">
              <h3>Risk Factor</h3>
              <p>${analysis.aiInsights.riskFactor}</p>
            </div>
            <div class="insight">
              <h3>Opportunity</h3>
              <p>${analysis.aiInsights.opportunity}</p>
            </div>
          ` : ''}

          <p style="margin-top: 40px; text-align: center; color: #999;">
            Generated by VentureClone AI on ${new Date().toLocaleString()}
          </p>
        </body>
      </html>
    `;

    // Create a blob and download
    const blob = new Blob([htmlContent], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `analysis-${analysis.id.slice(0, 8)}.html`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const generateCSV = () => {
    const csvContent = [
      ['Field', 'Value'],
      ['URL', analysis.url],
      ['Business Model', analysis.businessModel || ''],
      ['Revenue Stream', analysis.revenueStream || ''],
      ['Target Market', analysis.targetMarket || ''],
      ['Overall Score', analysis.overallScore?.toString() || ''],
      ['Technical Complexity Score', analysis.scoreDetails?.technicalComplexity.score.toString() || ''],
      ['Market Opportunity Score', analysis.scoreDetails?.marketOpportunity.score.toString() || ''],
      ['Competitive Landscape Score', analysis.scoreDetails?.competitiveLandscape.score.toString() || ''],
      ['Resource Requirements Score', analysis.scoreDetails?.resourceRequirements.score.toString() || ''],
      ['Time to Market Score', analysis.scoreDetails?.timeToMarket.score.toString() || ''],
      ['Key Insight', analysis.aiInsights?.keyInsight || ''],
      ['Risk Factor', analysis.aiInsights?.riskFactor || ''],
      ['Opportunity', analysis.aiInsights?.opportunity || ''],
      ['Current Stage', analysis.currentStage.toString()],
      ['Created At', new Date(analysis.createdAt).toLocaleString()],
    ].map(row => row.map(cell => `"${cell}"`).join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `analysis-${analysis.id.slice(0, 8)}.csv`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const generateJSON = () => {
    const exportData = {
      analysis,
      stages,
      exportedAt: new Date().toISOString(),
      version: '1.0.0'
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `analysis-${analysis.id.slice(0, 8)}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleExport = async (format: 'pdf' | 'csv' | 'json') => {
    setIsExporting(true);
    try {
      switch (format) {
        case 'pdf':
          generatePDF();
          toast({
            title: "Export Successful",
            description: "Analysis exported as HTML report",
          });
          break;
        case 'csv':
          generateCSV();
          toast({
            title: "Export Successful",
            description: "Analysis exported as CSV",
          });
          break;
        case 'json':
          generateJSON();
          toast({
            title: "Export Successful",
            description: "Analysis exported as JSON",
          });
          break;
      }
    } catch (error) {
      toast({
        title: "Export Failed",
        description: "Failed to export analysis",
        variant: "destructive",
      });
    } finally {
      setIsExporting(false);
    }
  };

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button
          variant="outline"
          size="sm"
          className="bg-vc-card border-vc-border text-vc-text hover:border-vc-primary"
          disabled={isExporting}
          data-testid="button-export-menu"
        >
          <Download className="mr-2 h-4 w-4" />
          {isExporting ? 'Exporting...' : 'Export'}
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent className="bg-vc-card border-vc-border">
        <DropdownMenuItem
          onClick={() => handleExport('pdf')}
          className="text-vc-text hover:bg-vc-dark cursor-pointer"
          data-testid="export-pdf"
        >
          <FileText className="mr-2 h-4 w-4" />
          Export as Report (HTML)
        </DropdownMenuItem>
        <DropdownMenuItem
          onClick={() => handleExport('csv')}
          className="text-vc-text hover:bg-vc-dark cursor-pointer"
          data-testid="export-csv"
        >
          <FileSpreadsheet className="mr-2 h-4 w-4" />
          Export as CSV
        </DropdownMenuItem>
        <DropdownMenuItem
          onClick={() => handleExport('json')}
          className="text-vc-text hover:bg-vc-dark cursor-pointer"
          data-testid="export-json"
        >
          <FileJson className="mr-2 h-4 w-4" />
          Export as JSON
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

