import { CheckCircle } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import type { BusinessAnalysis } from "@/types";

interface ProgressTrackerProps {
  analysis: BusinessAnalysis;
}

const STAGES = [
  { number: 1, name: 'Discovery & Selection' },
  { number: 2, name: 'Lazy-Entrepreneur Filter' },
  { number: 3, name: 'MVP Launch Planning' },
  { number: 4, name: 'Demand Testing' },
  { number: 5, name: 'Scaling & Growth' },
  { number: 6, name: 'AI Automation' },
];

export function ProgressTracker({ analysis }: ProgressTrackerProps) {
  const currentStage = analysis.currentStage || 1;

  const getStageStatus = (stageNumber: number) => {
    if (stageNumber < currentStage) return 'completed';
    if (stageNumber === currentStage) return 'current';
    return 'pending';
  };

  return (
    <Card className="bg-vc-card border-vc-border" data-testid="card-progress-tracker">
      <CardHeader>
        <CardTitle className="text-lg font-semibold text-vc-text">Progress Tracker</CardTitle>
      </CardHeader>
      <CardContent className="space-y-3">
        {STAGES.map((stage) => {
          const status = getStageStatus(stage.number);

          return (
            <div key={stage.number} className="flex items-center space-x-3" data-testid={`progress-stage-${stage.number}`}>
              <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                status === 'completed' 
                  ? 'bg-vc-primary' 
                  : status === 'current'
                  ? 'bg-vc-accent'
                  : 'bg-vc-border'
              }`}>
                {status === 'completed' ? (
                  <CheckCircle className="h-4 w-4 text-white" />
                ) : (
                  <span className={`text-xs font-semibold ${
                    status === 'current' ? 'text-vc-dark' : 'text-vc-text-muted'
                  }`}>
                    {stage.number}
                  </span>
                )}
              </div>
              <div className="flex-1">
                <p className={`text-sm font-medium ${
                  status === 'pending' ? 'text-vc-text-muted' : 'text-vc-text'
                }`}>
                  {stage.name}
                </p>
                <p className="text-xs text-vc-text-muted">
                  {status === 'completed' ? 'Completed' : 
                   status === 'current' ? 'In Progress' : 'Pending'}
                </p>
              </div>
            </div>
          );
        })}
      </CardContent>
    </Card>
  );
}

client/src/components/quick-stats.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { TrendingUp, Target, Clock, Zap } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import type { QuickStats as QuickStatsType } from "@/types";

export function QuickStats() {
  const { data: stats, isLoading, error } = useQuery({
    queryKey: ['/api/stats'],
    queryFn: async (): Promise<QuickStatsType> => {
      const response = await fetch('/api/stats');
      if (!response.ok) {
        throw new Error('Failed to fetch stats');
      }
      return response.json();
    },
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  const statsItems = [
    {
      key: 'totalAnalyses',
      label: 'Total Analyses',
      icon: TrendingUp,
      value: stats?.totalAnalyses || 0,
      color: 'text-vc-text',
    },
    {
      key: 'strongCandidates',
      label: 'Strong Candidates',
      icon: Target,
      value: stats?.strongCandidates || 0,
      color: 'text-green-400',
    },
    {
      key: 'inProgress',
      label: 'In Progress',
      icon: Clock,
      value: stats?.inProgress || 0,
      color: 'text-vc-accent',
    },
    {
      key: 'aiQueries',
      label: 'AI Queries Used',
      icon: Zap,
      value: stats?.aiQueries || 0,
      color: 'text-vc-text',
    },
  ];

  const formatNumber = (num: number) => {
    if (num >= 1000000) {
      return `${(num / 1000000).toFixed(1)}M`;
    }
    if (num >= 1000) {
      return `${(num / 1000).toFixed(1)}K`;
    }
    return num.toLocaleString();
  };

  if (error) {
    return (
      <Card className="bg-vc-card border-vc-border" data-testid="card-quick-stats">
        <CardHeader>
          <CardTitle className="text-lg font-semibold text-vc-text">Quick Stats</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-4">
            <p className="text-vc-text-muted text-sm">Failed to load stats</p>
            <p className="text-xs text-vc-text-muted mt-1">
              Please try refreshing the page
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-vc-card border-vc-border" data-testid="card-quick-stats">
      <CardHeader>
        <CardTitle className="text-lg font-semibold text-vc-text">Quick Stats</CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {statsItems.map((item) => {
          const Icon = item.icon;
          const displayValue = isLoading ? '-' : formatNumber(item.value);

          return (
            <div
              key={item.key}
              className="flex justify-between items-center"
              data-testid={`stat-${item.key}`}
            >
              <div className="flex items-center space-x-2">
                <Icon className={`h-4 w-4 ${item.color}`} />
                <span className="text-vc-text-muted text-sm">{item.label}</span>
              </div>
              <span className={`font-semibold ${item.color} ${isLoading ? 'animate-pulse' : ''}`}>
                {displayValue}
              </span>
            </div>
          );
        })}

        {/* Progress indicator for AI queries if applicable */}
        {stats && stats.aiQueries > 0 && (
          <div className="pt-2 border-t border-vc-border/50">
            <div className="flex justify-between items-center text-xs text-vc-text-muted mb-1">
              <span>API Usage</span>
              <span>{Math.min(100, Math.round((stats.aiQueries / 10000) * 100))}%</span>
            </div>
            <div className="w-full bg-vc-border rounded-full h-1">
              <div
                className="bg-vc-primary h-1 rounded-full transition-all duration-300"
                style={{
                  width: `${Math.min(100, (stats.aiQueries / 10000) * 100)}%`,
                }}
              ></div>
            </div>
            <p className="text-xs text-vc-text-muted mt-1">
              Monthly quota usage
            </p>
          </div>
        )}

        {/* Quick insights based on stats */}
        {stats && stats.totalAnalyses > 0 && (
          <div className="pt-2 border-t border-vc-border/50">
            <div className="text-xs text-vc-text-muted space-y-1">
              {stats.strongCandidates > 0 && (
                <div className="flex items-center space-x-1">
                  <div className="w-1.5 h-1.5 bg-green-400 rounded-full"></div>
                  <span>
                    {Math.round((stats.strongCandidates / stats.totalAnalyses) * 100)}% success rate
                  </span>
                </div>
              )}
              {stats.inProgress > 0 && (
                <div className="flex items-center space-x-1">
                  <div className="w-1.5 h-1.5 bg-vc-accent rounded-full animate-pulse"></div>
                  <span>
                    {stats.inProgress} project{stats.inProgress !== 1 ? 's' : ''} in development
                  </span>
                </div>
              )}
              {stats.totalAnalyses >= 10 && (
                <div className="flex items-center space-x-1">
                  <div className="w-1.5 h-1.5 bg-vc-primary rounded-full"></div>
                  <span>Active researcher ðŸš€</span>
                </div>
              )}
            </div>
          </div>
        )}

        {/* Empty state for new users */}
        {!isLoading && (!stats || stats.totalAnalyses === 0) && (
          <div className="text-center py-4 border-t border-vc-border/50">
            <div className="text-2xl mb-2">ðŸš€</div>
            <p className="text-vc-text-muted text-sm">
              Start analyzing businesses to see your stats
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/recent-analyses.ts.content:
import { useQuery } from "@tanstack/react-query";
import { ArrowRight, ExternalLink } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import type { BusinessAnalysis } from "@/types";

interface RecentAnalysesProps {
  analyses: BusinessAnalysis[];
  onAnalysisSelect: (analysis: BusinessAnalysis) => void;
}

export function RecentAnalyses({ analyses, onAnalysisSelect }: RecentAnalysesProps) {
  const formatTimeAgo = (date: Date) => {
    const now = new Date();
    const diffInMs = now.getTime() - new Date(date).getTime();
    const diffInHours = diffInMs / (1000 * 60 * 60);
    const diffInDays = diffInMs / (1000 * 60 * 60 * 24);

    if (diffInHours < 1) {
      const minutes = Math.floor(diffInMs / (1000 * 60));
      return `${minutes} minute${minutes !== 1 ? 's' : ''} ago`;
    } else if (diffInHours < 24) {
      const hours = Math.floor(diffInHours);
      return `${hours} hour${hours !== 1 ? 's' : ''} ago`;
    } else if (diffInDays < 7) {
      const days = Math.floor(diffInDays);
      return `${days} day${days !== 1 ? 's' : ''} ago`;
    } else {
      return new Date(date).toLocaleDateString();
    }
  };

  const getBusinessIcon = (businessModel?: string) => {
    if (!businessModel) return 'ðŸ¢';

    const model = businessModel.toLowerCase();
    if (model.includes('saas') || model.includes('software')) return 'ðŸ’»';
    if (model.includes('ecommerce') || model.includes('marketplace')) return 'ðŸ›’';
    if (model.includes('social') || model.includes('network')) return 'ðŸ‘¥';
    if (model.includes('education') || model.includes('learning')) return 'ðŸ“š';
    if (model.includes('finance') || model.includes('fintech')) return 'ðŸ’³';
    if (model.includes('health') || model.includes('medical')) return 'ðŸ¥';
    if (model.includes('media') || model.includes('content')) return 'ðŸ“º';
    return 'ðŸ¢';
  };

  const getScoreColor = (score?: number) => {
    if (!score) return 'text-vc-text-muted';
    if (score >= 7) return 'text-green-400';
    if (score >= 5) return 'text-yellow-400';
    return 'text-red-400';
  };

  const getScoreBadgeColor = (score?: number) => {
    if (!score) return 'bg-vc-border text-vc-text-muted';
    if (score >= 7) return 'bg-green-900/50 text-green-300';
    if (score >= 5) return 'bg-yellow-900/50 text-yellow-300';
    return 'bg-red-900/50 text-red-300';
  };

  if (!analyses || analyses.length === 0) {
    return (
      <Card className="bg-vc-card border-vc-border" data-testid="card-recent-analyses">
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-lg font-semibold text-vc-text">Recent Analyses</CardTitle>
          </div>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8">
            <div className="text-4xl mb-4">ðŸ“Š</div>
            <p className="text-vc-text-muted">No analyses yet</p>
            <p className="text-vc-text-muted text-sm mt-1">
              Start by analyzing a business URL above
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  // Show most recent 5 analyses
  const recentAnalyses = analyses.slice(0, 5);

  return (
    <Card className="bg-vc-card border-vc-border" data-testid="card-recent-analyses">
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg font-semibold text-vc-text">Recent Analyses</CardTitle>
          {analyses.length > 5 && (
            <Button
              variant="ghost"
              size="sm"
              className="text-vc-text-muted hover:text-vc-text transition-colors"
              data-testid="button-view-all-analyses"
            >
              <ExternalLink className="h-4 w-4" />
            </Button>
          )}
        </div>
      </CardHeader>
      <CardContent className="space-y-3">
        {recentAnalyses.map((analysis) => (
          <div
            key={analysis.id}
            className="flex items-center justify-between p-3 bg-vc-dark rounded-lg border border-vc-border/50 hover:border-vc-border transition-colors cursor-pointer"
            onClick={() => onAnalysisSelect(analysis)}
            data-testid={`analysis-item-${analysis.id}`}
          >
            <div className="flex items-center space-x-3 flex-1 min-w-0">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-blue-600 rounded-lg flex items-center justify-center flex-shrink-0">
                <span className="text-lg">
                  {getBusinessIcon(analysis.businessModel)}
                </span>
              </div>
              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-2 mb-1">
                  <p className="text-vc-text font-medium truncate">
                    {analysis.businessModel || 'Business Analysis'}
                  </p>
                  {analysis.overallScore && (
                    <Badge className={`${getScoreBadgeColor(analysis.overallScore)} text-xs font-medium`}>
                      {analysis.overallScore}/10
                    </Badge>
                  )}
                </div>
                <div className="flex items-center space-x-2">
                  <p className="text-vc-text-muted text-sm truncate">
                    {analysis.targetMarket || analysis.revenueStream || 'No details'}
                  </p>
                  <span className="text-vc-text-muted">â€¢</span>
                  <span className="text-xs text-vc-text-muted whitespace-nowrap">
                    {formatTimeAgo(analysis.createdAt)}
                  </span>
                </div>
                <p className="text-xs text-vc-text-muted truncate mt-1 font-mono">
                  {analysis.url}
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-2 flex-shrink-0 ml-3">
              <div className="flex items-center space-x-1">
                <div className={`w-2 h-2 rounded-full ${
                  (analysis.currentStage || 1) === 6 ? 'bg-green-400' :
                  (analysis.currentStage || 1) > 1 ? 'bg-vc-accent animate-pulse' :
                  'bg-vc-border'
                }`}></div>
                <span className="text-xs text-vc-text-muted">
                  Stage {analysis.currentStage || 1}
                </span>
              </div>
              <Button
                variant="ghost"
                size="icon"
                className="text-vc-primary hover:text-vc-primary/80 transition-colors h-8 w-8"
                onClick={(e) => {
                  e.stopPropagation();
                  onAnalysisSelect(analysis);
                }}
                data-testid={`button-load-analysis-${analysis.id}`}
              >
                <ArrowRight className="h-4 w-4" />
              </Button>
            </div>
          </div>
        ))}

        {analyses.length > 5 && (
          <div className="text-center pt-2">
            <p className="text-xs text-vc-text-muted">
              Showing {recentAnalyses.length} of {analyses.length} analyses
            </p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/search-filter.tsx.content:
import { useState } from "react";
import { Search, Filter, SlidersHorizontal, X } from "lucide-react";
import {
  Input,
  Button,
  Badge,
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
  Slider,
  Popover,
  PopoverContent,
  PopoverTrigger,
  Label
} from "@/components/ui";
import type { BusinessAnalysis, SearchFilterProps, FilterOptions } from "@/types";

interface SearchFilterProps {
  analyses: BusinessAnalysis[];
  onFilteredResults: (filtered: BusinessAnalysis[]) => void;
}

export function SearchFilter({ analyses, onFilteredResults }: SearchFilterProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [scoreRange, setScoreRange] = useState([0, 10]);
  const [stageFilter, setStageFilter] = useState<string>("all");
  const [sortBy, setSortBy] = useState<string>("date");
  const [activeFilters, setActiveFilters] = useState<string[]>([]);

  const applyFilters = () => {
    let filtered = [...analyses];

    // Apply search filter
    if (searchTerm) {
      filtered = filtered.filter(
        (a) =>
          a.url.toLowerCase().includes(searchTerm.toLowerCase()) ||
          a.businessModel?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          a.targetMarket?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          a.revenueStream?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply score range filter
    filtered = filtered.filter(
      (a) =>
        (a.overallScore || 0) >= scoreRange[0] &&
        (a.overallScore || 0) <= scoreRange[1]
    );

    // Apply stage filter
    if (stageFilter !== "all") {
      filtered = filtered.filter(
        (a) => a.currentStage === parseInt(stageFilter)
      );
    }

    // Apply sorting
    filtered.sort((a, b) => {
      switch (sortBy) {
        case "score-high":
          return (b.overallScore || 0) - (a.overallScore || 0);
        case "score-low":
          return (a.overallScore || 0) - (b.overallScore || 0);
        case "date-new":
          return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
        case "date-old":
          return new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime();
        case "stage-high":
          return (b.currentStage || 1) - (a.currentStage || 1);
        case "stage-low":
          return (a.currentStage || 1) - (b.currentStage || 1);
        default:
          return 0;
      }
    });

    // Update active filters display
    const filters = [];
    if (searchTerm) filters.push(`Search: "${searchTerm}"`);
    if (scoreRange[0] > 0 || scoreRange[1] < 10) {
      filters.push(`Score: ${scoreRange[0]}-${scoreRange[1]}`);
    }
    if (stageFilter !== "all") {
      filters.push(`Stage: ${stageFilter}`);
    }
    setActiveFilters(filters);

    onFilteredResults(filtered);
  };

  const clearFilters = () => {
    setSearchTerm("");
    setScoreRange([0, 10]);
    setStageFilter("all");
    setSortBy("date");
    setActiveFilters([]);
    onFilteredResults(analyses);
  };

  // Auto-apply filters when values change
  React.useEffect(() => {
    applyFilters();
  }, [searchTerm, scoreRange, stageFilter, sortBy]);

  return (
    <div className="space-y-4" data-testid="search-filter">
      {/* Search Bar */}
      <div className="flex gap-2">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-vc-text-muted" />
          <Input
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search by URL, business model, market, or revenue stream..."
            className="pl-10 bg-vc-dark border-vc-border text-vc-text placeholder:text-vc-text-muted"
            data-testid="input-search"
          />
        </div>

        {/* Advanced Filters */}
        <Popover>
          <PopoverTrigger asChild>
            <Button
              variant="outline"
              className="bg-vc-card border-vc-border text-vc-text hover:border-vc-primary"
              data-testid="button-filters"
            >
              <SlidersHorizontal className="h-4 w-4 mr-2" />
              Filters
              {activeFilters.length > 0 && (
                <Badge className="ml-2 bg-vc-primary/20 text-vc-primary">
                  {activeFilters.length}
                </Badge>
              )}
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-80 bg-vc-card border-vc-border">
            <div className="space-y-4">
              <div className="space-y-2">
                <Label className="text-vc-text">Score Range</Label>
                <div className="flex items-center space-x-3">
                  <span className="text-vc-text-muted text-sm">{scoreRange[0]}</span>
                  <Slider
                    value={scoreRange}
                    onValueChange={setScoreRange}
                    max={10}
                    step={1}
                    className="flex-1"
                    data-testid="slider-score"
                  />
                  <span className="text-vc-text-muted text-sm">{scoreRange[1]}</span>
                </div>
              </div>

              <div className="space-y-2">
                <Label className="text-vc-text">Workflow Stage</Label>
                <Select value={stageFilter} onValueChange={setStageFilter}>
                  <SelectTrigger className="bg-vc-dark border-vc-border text-vc-text" data-testid="select-stage">
                    <SelectValue placeholder="All Stages" />
                  </SelectTrigger>
                  <SelectContent className="bg-vc-card border-vc-border">
                    <SelectItem value="all">All Stages</SelectItem>
                    <SelectItem value="1">1 - Discovery</SelectItem>
                    <SelectItem value="2">2 - Filter</SelectItem>
                    <SelectItem value="3">3 - MVP</SelectItem>
                    <SelectItem value="4">4 - Testing</SelectItem>
                    <SelectItem value="5">5 - Scaling</SelectItem>
                    <SelectItem value="6">6 - Automation</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label className="text-vc-text">Sort By</Label>
                <Select value={sortBy} onValueChange={setSortBy}>
                  <SelectTrigger className="bg-vc-dark border-vc-border text-vc-text" data-testid="select-sort">
                    <SelectValue placeholder="Sort by..." />
                  </SelectTrigger>
                  <SelectContent className="bg-vc-card border-vc-border">
                    <SelectItem value="date-new">Date (Newest)</SelectItem>
                    <SelectItem value="date-old">Date (Oldest)</SelectItem>
                    <SelectItem value="score-high">Score (High to Low)</SelectItem>
                    <SelectItem value="score-low">Score (Low to High)</SelectItem>
                    <SelectItem value="stage-high">Stage (6 to 1)</SelectItem>
                    <SelectItem value="stage-low">Stage (1 to 6)</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <Button
                onClick={clearFilters}
                variant="outline"
                className="w-full border-vc-border text-vc-text hover:bg-vc-dark"
                data-testid="button-clear-filters"
              >
                <X className="h-4 w-4 mr-2" />
                Clear All Filters
              </Button>
            </div>
          </PopoverContent>
        </Popover>

        {/* Sort Dropdown */}
        <Select value={sortBy} onValueChange={setSortBy}>
          <SelectTrigger className="w-[180px] bg-vc-card border-vc-border text-vc-text" data-testid="select-quick-sort">
            <Filter className="h-4 w-4 mr-2" />
            <SelectValue placeholder="Sort by..." />
          </SelectTrigger>
          <SelectContent className="bg-vc-card border-vc-border">
            <SelectItem value="date-new">Newest First</SelectItem>
            <SelectItem value="date-old">Oldest First</SelectItem>
            <SelectItem value="score-high">Highest Score</SelectItem>
            <SelectItem value="score-low">Lowest Score</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Active Filters Display */}
      {activeFilters.length > 0 && (
        <div className="flex flex-wrap gap-2">
          {activeFilters.map((filter, index) => (
            <Badge
              key={index}
              variant="secondary"
              className="bg-vc-primary/20 text-vc-primary border-vc-primary/30"
            >
              {filter}
            </Badge>
          ))}
          <Button
            onClick={clearFilters}
            variant="ghost"
            size="sm"
            className="h-6 px-2 text-vc-text-muted hover:text-vc-text"
            data-testid="button-clear-inline"
          >
            Clear all
          </Button>
        </div>
      )}

      {/* Results Count */}
      <div className="text-sm text-vc-text-muted">
        Showing {analyses.length} {analyses.length === 1 ? 'result' : 'results'}
      </div>
    </div>
  );
}

